				return parent;
			}
			parent = parent.$parent
		}
		return null;
	},
	getIdComDiv(req) {
		return req.split('^:^');
	},
	priceToString(price)  {
		return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
	}
